import * as Three from "three";

export const buildScene = (el) => {
    let scene = new Three.Scene();
    let camera = new Three.PerspectiveCamera(75, 1, 1, 1000);
    let renderer = new Three.WebGLRenderer();

    el.appendChild(renderer.domElement);

    let floorGeometry = new Three.BoxGeometry(10, 1, 10);
    let floorMaterial = new Three.MeshStandardMaterial();
    let floor = new Three.Mesh(floorGeometry, floorMaterial);
    floor.name = "floor";

    let cubeGeometry = new Three.BoxGeometry(1, 1, 1);
    let cubeMaterial = new Three.MeshNormalMaterial();
    let cube = new Three.Mesh(cubeGeometry, cubeMaterial);
    cube.name = 'cube';
    
    let light = new Three.AmbientLight("blue");

    cube.position.y += 2;
    scene.add(light);
    scene.add(floor);
    scene.add(cube);
    camera.position.z = 5;
    camera.position.y = 2;

    run(scene, renderer, camera );
}

const fitCanvasToDisplaySize = (force, renderer, camera) => {
    if(!renderer) return;
    const canvas = renderer.domElement;
    const width = canvas.clientWidth;
    const height = canvas.clientHeight;

    if (force || canvas.width !== width || canvas.height !== height) {
      renderer.setSize(width, height, false);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    }   
}

const animate = (scene, renderer, camera) => {
    if(!renderer) return;
    fitCanvasToDisplaySize(false, renderer, camera);

    //animate stuff here--
    let cube = scene.getObjectByName( "cube" );
    cube.rotation.y += 0.01;
    cube.rotation.x += 0.01;
    
    //--
    renderer.render(scene, camera);
    requestAnimationFrame(animate);
}

const run = (scene, renderer, camera) => {
    animate(scene, renderer, camera);
    fitCanvasToDisplaySize(true, renderer, camera);
    requestAnimationFrame(animate);
}
