import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as Three from "three";
import "./index.css";
import { RGBA_ASTC_10x5_Format } from "three";

class App extends Component {
  componentDidMount() {
	let fitCanvasToDisplaySize = (force, renderer, camera) => {
		const canvas = renderer.domElement;
		const width = canvas.clientWidth;
		const height = canvas.clientHeight;

		if (force || canvas.width !== width || canvas.height !== height) {
		  renderer.setSize(width, height, false);
		  camera.aspect = width / height;
		  camera.updateProjectionMatrix();
		}
	}

    let scene = new Three.Scene();
    let camera = new Three.PerspectiveCamera(75, 1, 1, 1000);
    let renderer = new Three.WebGLRenderer();

    this.el.appendChild(renderer.domElement);

    let floorGeometry = new Three.BoxGeometry(10, 1, 10);
    let floorMaterial = new Three.MeshStandardMaterial();
    let floor = new Three.Mesh(floorGeometry, floorMaterial);

    let circleGeometry = new Three.CircleGeometry(1, 50, 1, 1);
    let circleMaterial = new Three.MeshStandardMaterial();
    let circle = new Three.Mesh(circleGeometry, circleMaterial);

    let light = new Three.AmbientLight("blue");

    scene.add(light);
    scene.add(floor);
    scene.add(circle);
    camera.position.z = 5;
    camera.position.y = 2;



    let animate = function() {
      fitCanvasToDisplaySize(false, renderer, camera);

	  renderer.render(scene, camera);
	  requestAnimationFrame(animate);
    };

    fitCanvasToDisplaySize(true, renderer, camera);
    requestAnimationFrame(animate);
  }
  render() {
    return <div ref={ref => (this.el = ref)}/>;
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
