import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as Three from "three";
class App extends Component {
	componentWillMount(){
		window.addEventListener( 'resize', onWindowResize, false);
	}
	componentDidMount(){

		const onWindowResize = () => {
			camera.aspect = window.innerWidth / window.innerHeight;
			camera.updateProjectionMatrix();

			renderer.setSize( window.innerWidth, window.innerHeight);

		}
		let scene = new Three.Scene();
		let camera = new Three.PerspectiveCamera(75, window.innerWidth/window.innerHeight, 0.1, 1000);
		let renderer = new Three.WebGLRenderer();

		renderer.setSize( window.innerWidth, window.innerHeight);
		this.mount.appendChild( renderer.domElement)

		let floorGeometry = new Three.BoxGeometry( 10, 1, 10);
		let floorMaterial = new Three.MeshStandardMaterial();
		let floor = new Three.Mesh(floorGeometry, floorMaterial);

		let circleGeometry = new Three.CircleGeometry(1, 50, 1, 1);
		let circleMaterial = new Three.MeshStandardMaterial();
		let circle = new Three.Mesh(circleGeometry, circleMaterial);

		let light = new Three.AmbientLight("white");

		scene.add(light);
		scene.add(floor);
		scene.add(circle);
		camera.position.z = 5;
		camera.position.y = 2;

		let animate = function(){
			requestAnimationFrame(animate);

			renderer.render(scene, camera);
		}
		animate();
	}
	render() {
    	return (
      		<div ref={ref => (this.mount = ref)}/>
    	);
  }
}
const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
